import React, { useState } from 'react'
import "./login.css"
import axios from 'axios'
import { useNavigate } from "react-router-dom"

const Login = ({setLoginUser}) => {

    const navigate = useNavigate()

    const [ user, setUser] = useState({
        gituser: "",
        password: ""
    })

    const handleChange = e =>{
        const {name,value}=e.target
        setUser({
            ...user,
            [name]: value
        })
    }

    const login = () => {
        axios.post("http://localhost:9000/login", user)
        .then(res => {
            alert(res.data.message)
            setLoginUser(res.data.user)
            navigate("/user")
        })
    }

    return(
        <div className="login">
            <h1>Ingresar</h1>
            <input type="text" name='gituser' value={user.gituser} placeholder="Github username" onChange={ handleChange }></input>
            <input type="password" name='password' value={user.password} placeholder="Ingresa ContraseÃ±a" onChange={ handleChange }></input>
            <button type="button" className="btn btn-primary" onClick={login}>Ingresar</button>
            <a href='https://github.com/login/oauth/authorize?client_id=22d94d324bf35bb94d9b'><button type="button" className="btn btn-primary" >Login with Github</button></a>
            <div>o</div>
            <button type="button" className="btn btn-primary" onClick={() => navigate("/register")}>Register</button>
        </div>
    )
}

export default Login
