import React, { useState } from 'react'
import "./login.css"
import axios from 'axios'
import { useNavigate } from "react-router-dom"

const Login = ({setLoginUser}) => {

    const navigate = useNavigate()

    const [ user, setUser] = useState({
        gituser: "",
        password: ""
    })

    const handleChange = e =>{
        const {name,value}=e.target
        setUser({
            ...user,
            [name]: value
        })
    }

    const login = () => {
        axios.post("http://localhost:9000/login", user)
        .then(res => {
            alert(res.data.message)
            setLoginUser(res.data.user)
            switch(res.data.message){
                case 'CFU':
                    document.getElementById('cfu').classList.add('show');
                    setTimeout(function(){
                        document.getElementById('cfu').classList.remove('show');
                   }, 3200);
                break;
            }
            if(res.data.user){
            navigate("/user")
            }
        })
    }

    return(
        <div className="login">
            <h1>Ingresar</h1>
            <input type="text" name='gituser' value={user.gituser} placeholder="Github username" onChange={ handleChange }></input>
            <input type="password" name='password' value={user.password} placeholder="Ingresa ContraseÃ±a" onChange={ handleChange }></input>
            <button type="button" className="btn btn-primary" onClick={login}>Ingresar</button>
            <a href='https://github.com/login/oauth/authorize?client_id=22d94d324bf35bb94d9b'><button type="button" className="btn btn-dark" >Login with <i className="fa-brands fa-github"></i> Github</button></a>
            <div>or</div>
            <button type="button" className="btn btn-primary" onClick={() => navigate("/register")}>Register</button>
            <div id='cfu' className="alert-box failure">Failure Alert !!!</div>
        </div>
    )
}

export default Login
