import React, { useState, useEffect } from 'react';
import "./homepage.css"
import { useParams } from 'react-router-dom';
import axios from 'axios'

const Homepage = ({setLoginUser, user}) => {
    const [repos, setRepos] = useState([]);
    const [favorites, setFavorites] = useState([]);
    const { username } = useParams();
    var userGit = username?username:user.gituser;
    

    useEffect(() => {
        async function gitrepo() {
            const  result  = await axios(`https://api.github.com/users/${userGit}/repos`);
            setRepos(result.data);
        }
        gitrepo();
      }, []);

      const listRepos = 
        repos.length>0?(
            repos.map((item) => <li key={item.id}>{item.name} - <i title='Add to Favorites' className="fa-solid fa-star" onClick={() => {
                setFavorites([
                  ...favorites,
                  { name: item.name, id: item.id },
                ]);
              }}></i></li>)
        ) : (
            <li>No repos found with the username {userGit}</li>
        )


        const listFav = 
        favorites.length>0?(
            favorites.map((item) => <li key={item.id}>{item.name} - <i title='Remove from Favorites' className="fa-solid fa-circle-minus" onClick={() => {
                setFavorites([
                  ...favorites.filter(
                    (rep) => rep.name !== item.name
                  ),
                ]);
              }}></i></li>)
        ) : (
            <li>No favorite repos</li>
        )
        
    //   console.log(repos)
    return(
        <div className='homepage'>
            <h1>Hi {user.name}</h1>
            <div className='button' onClick={() => setLoginUser({})}>Logout</div>
            <hr />

            <div className='repos'>
                <div style={{display: "table-cell"}}>
                    <h3>All your repos ({user.gituser})</h3>
                    <ul>
                        {listRepos}
                    </ul>
                </div>
                
                <ul style={{display: "table-cell"}}>
                    <h3>Favorite Repos</h3>
                    <ul>
                        {listFav}
                    </ul>
                </ul>
            </div>
        </div>
    )
}

export default Homepage
