import React, { useState } from 'react'
import "./register.css"
import axios from 'axios'
import { useNavigate } from "react-router-dom"

const Register = () => {

    const navigate = useNavigate()

    const [ user, setUser] = useState({
        name: "",
        gituser : "",
        password: ""
    })

    const handleChange = e =>{
        const {name,value}=e.target
        setUser({
            ...user,
            [name]: value
        })
    }

    const register = () => {
        const {name,password} = user 
        if (name && password){
            axios.post("http://localhost:9002/register", user)
            .then( res => {
                alert(res.data.message)
                navigate("/login")
            })
        }else{
            alert("Llena los campos")
        }
    }

    return(
        <div className="register">
            <h1>Registrarse</h1>
            <input type="text" name='name' value={user.name} placeholder="Ingresa Nombre" onChange={ handleChange }></input>
            <input type="password" name='password' value={user.password} placeholder="Ingresa ContraseÃ±a" onChange={ handleChange }></input>
            <div className="button" onClick={register}>Registrar</div>
            <div>o</div>
            <div className="button" onClick={() => navigate("/login")}>Ingresar</div>
        </div>
    )
}

export default Register
