import React, { useState } from 'react'
import "./login.css"
import axios from 'axios'
import { useNavigate } from "react-router-dom"

const Login = ({setLoginUser}) => {

    const navigate = useNavigate()

    const [ user, setUser] = useState({
        name: "",
        password: ""
    })

    const handleChange = e =>{
        const {name,value}=e.target
        setUser({
            ...user,
            [name]: value
        })
    }

    const login = () => {
        axios.post("http://localhost:9002/login", user)
        .then(res => {
            res.data.user?null:alert(res.data.message);
            setLoginUser(res.data.user)
            navigate("/")
        })
    }

    return(
        <div className="login">
            <h1>Ingresar</h1>
            <input type="text" name='name' value={user.name} placeholder="Ingresa Nombre" onChange={ handleChange }></input>
            <input type="password" name='password' value={user.password} placeholder="Ingresa ContraseÃ±a" onChange={ handleChange }></input>
            <div className="button" onClick={login}>Ingresar</div>
            <div>o</div>
            <div className="button" onClick={() => navigate("/register")}>Register</div>
        </div>
    )
}

export default Login
